import { useState } from 'react';
import './style.css';

const ChatComponent = (props) => {
    const [messages, setMessages] = useState(
        [
            {
                name: 'A',
                msg: 'message 1',
                time: new Date()
            },
            {
                name: 'B',
                msg: 'message 2',
                time: new Date()
            },
            {
                name: 'C',
                msg: 'message 3',
                time: new Date()
            }
        ]
    );

    return (
        <div className='chat-container'>
            <div className='chat-header'>
                <h2>{`${props.chatname}`}</h2>
            </div>

            <div className='chat-messages-container'>
                {
                    messages.map((message:{...}, index: number) => {
                        return(
                            <ChatMessageComponent name={message.name} message={message.msg} time={message.time}></ChatMessageComponent>
                        );
                    })
                }

                <div className='line-with-text-container'>
                    <p className='line-with-text'><span>Oct 4, 2021</span></p>
                </div>
            </div>

            <form className='input-container' onSubmit={(e) => {
                e.preventDefault(); console.log('send data to server');
            }}>
                <input placeholder='Type a new message...'></input>
                <button><strong>{'>>'}</strong></button>
            </form>
        </div>
    );
}

const ChatMessageComponent = (props) => {
    return (
        <div className='chat-message-container'>
            <p><strong>{`${props.name} | ${props.message}`}</strong></p>
            <p className='float-right'>{`${props.time}`}</p>
        </div>
    );
}

export default ChatComponent;
